    @Override
    public boolean passwordCheck(String password) {
        if (password.length() > 5) {
            return  true;
        }
        System.out.println("Password length must be bigger than 5 character");
        return false;
    }

    @Override
    public boolean fullNameCheck(String firstName, String lastName) {
        if (firstName.length() > 1 && lastName.length() > 1) {
            return  true;
        }
        System.out.println("First name and last name  length must be bigger than 1 character");
        return false;
    }


